<template lang="pug">
    div.list-container
        transition(name="expand")
            UserEdit(
                v-if="editMode" 
                v-bind:editModeToggle="editModeToggle"
                v-bind:user='editUser'
                v-bind:posts='editPosts'
            )
        button.arrow.left-button(@click="moveCarousel(-1)" :disabled="atHeadOfList")
        button.arrow.right-button(@click="moveCarousel(1)" :disabled="atEndOfList")
        div.list-wrapper(v-for='user in users' :style="{ transform: 'translateX' + '(' + offset + 'px' + ')'}")
            User(
                v-bind:project='user'
                v-bind:editModeToggle="editModeToggle"
            )
</template>

<script>
    import axios from 'axios';
    import User from './User.vue';
    import UserEdit from './UserEdit.vue';
    export default {
        name: 'UserList',
        components: {
            User,
            UserEdit
        },
        data : () => {
            return {
                name: 'UserList',
                users: [],
                factor: 320,
                // factor: 960,
                offset: 0,
                amount: 3,
                editMode: false,
                // editMode: true,
                editUser: {},
                editPosts: [],
            }
        },
        created () {
            axios.get('http://localhost:9000/api/projects')
                .then(res => this.users = res.data)
                .catch(error => console.error(error));
        },
        computed: {
            atEndOfList() {
                return this.offset <= (this.factor * -1) * (this.users.length - this.amount);
            },
            atHeadOfList() {
                return this.offset === 0;
            },
        },
        methods: {
            moveCarousel(direction) {
                if (direction === 1) {
                    this.offset -= this.factor
                } else if (direction === -1) {
                    this.offset += this.factor
                }
            },
            editModeToggle(user, posts){

                if (this.editMode) {
                    this.editUser = {};
                    this.editPosts = [];
                    this.editMode = !this.editMode;
                    return
                }

                if (user && posts) {
                    this.editUser = user;
                    this.editPosts = posts;
                    this.editMode = !this.editMode;
                }


            }
        }
    }
</script>

